import React, { Fragment, useRef, useCallback, useState, useEffect } from 'react';

import classes from './AddMovie.module.css';

function AddMovie(props) {
    const titleRef = useRef('');
    const idRef = useRef('');
    const releaseDateRef = useRef('');
    const taglineRef = useRef('');
    const plotRef = useRef('');
    const durationRef = useRef('');
    const imageRef = useRef('');
    const capacityRef = useRef('');
    const genreRef = useRef('');

    const [movie, setMovie] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const temp = "tt12263384";

    const fetchMoviesHandler = useCallback(async () => {

        setIsLoading(true);
        setError(null);
        try {
            // console.log(idRef.current.value)
            const url = `https://imdb-api.com/en/API/Title/k_2e0orz7h/${idRef.current.value}`
            console.log(url)
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Something went wrong!');
            }
            const data = await response.json();
            console.log(data, " --- hiuijo")
            console.log(data.title);
            console.log(data.releaseDate);
            console.log(data.plot);
            console.log(data.imDbRating);
            console.log(data.genres);
            console.log(data.genreList);

            data.genreList.forEach(element => {
                console.log(element.value)
            });
            console.log(data.errorMessage)

            const transformedMovies =
            {
                id: data.id,
                title: data.title,
                plot: data.plot,
                tagline: data.tagline,
                releaseDate: data.releaseDate,
                rating: data.imDbRating,
                nowShowing: 'true',
                tagline: data.tagline,
                duration: data.runtimeStr,
                image: data.image,
                genres:data.genreList

                // ratingVotes: data.imDbRatingVotes
            };

            setMovie(transformedMovies);
        } catch (error) {
            setError(error.message);
        }
        setIsLoading(false);
    }, []);

    useEffect(() => {
        fetchMoviesHandler();
    }, [fetchMoviesHandler]);

    async function addMovieHandler(movie) {
        console.log(movie);
        const response = await fetch('https://movie-react-16994-default-rtdb.firebaseio.com/movie.json', {
            method: 'POST',
            body: JSON.stringify(movie),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const data = await response.json();
        console.log(data);
    };

    function submitHandler(event) {
        // event.preventDefault();

        // could add validation here...

        const movie = {
            id: idRef.current.value,
            title: titleRef.current.value,
            tagline: taglineRef.current.value,
            plot: plotRef.current.value,
            releaseDate: releaseDateRef.current.value,
            genre: genreRef.current.value,
            duration: durationRef.current.value,
            image: imageRef.current.value,
            capacity: capacityRef.current.value,
            genres:movie.genres
        };

        // props.onAddMovie(movie);

        addMovieHandler(movie)

    }

    return (
        <Fragment>
            <button style={{ marginLeft: "90px", marginTop: "25px" }} onClick={fetchMoviesHandler}>Fetch Data from Api</button>
            <form style={{ width: "500px", marginLeft: "90px" }} onSubmit={submitHandler}>
                <div className={classes.control} >
                    <label htmlFor='id'>IMDB id</label>
                    <input type='text' id='id' ref={idRef} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='title'>Title</label>
                    <input value={movie.title} type='text' id='title' ref={titleRef} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='tagline'>Tagline</label>
                    <input value={movie.tagline} type='text' id='tagline' ref={taglineRef} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='plot'>plot</label>
                    <textarea value={movie.plot} rows='5' id='plot' ref={plotRef}></textarea>
                </div>
                <div className={classes.control}>
                    <label htmlFor='date'>Release Date</label>
                    <input value={movie.releaseDate} type='text' id='date' ref={releaseDateRef} />
                </div>
                {movie.genres.forEach(genre => { 

                    <div className={classes.control}>
                        <label htmlFor='genre'>genre</label>
                        <input value={genre.value} type='text' id='genre'  />
                    </div>
                    })
                }
                
                <div className={classes.control}>
                    <label htmlFor='duration'>Duration</label>
                    <input value={movie.duration} type='text' id='duration' ref={durationRef} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='imageUrl'>Image</label>
                    <input value={movie.image} type='text' id='imageUrl' ref={imageRef} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='capacity'>Seat Capacity</label>
                    <input type='number' id='capacity' ref={capacityRef} />
                </div>
                <div className={classes.control}>
                    <label>
                        Showing Status
                        <select name="version" onChange={handleChange} value={state.version}>
                            <option value="true">True</option>
                            <option value="false">False</option>
                        </select>
                    </label>
                </div>
                <div className={classes.control}>
                    <label>
                        Showtime Slot
                        <select name="version" onChange={handleChange} value={state.version}>
                            <option >Null</option>
                            <option value='8-11 AM'>8-11 AM</option>
                            <option value='12-3 PM'>12-3 PM</option>
                            <option value='4-7 PM'>4-7 PM</option>
                            <option value='8-11 PM'>8-11 PM</option>
                        </select>
                    </label>
                </div>
                <div className={classes.control}>
                    <label>
                        Screen Number
                        <select name="version" onChange={handleChange} value={state.version}>
                            <option >Null</option>
                            <option value='1'>Screen 1</option>
                            <option value='2'>Screen 2</option>
                            <option value='3'>Screen 3</option>
                            <option value='4'>Screen 4</option>
                        </select>
                    </label>
                </div>
                <div className={classes.control}>
                    <label>
                        Ticket Price
                        <select name="version" onChange={handleChange} value={state.version}>
                            <option >Null</option>
                            <option value='10'>$ 10.00</option>
                            <option value='12'>$ 12.00</option>
                            <option value='15'>$ 15.00</option>
                            <option value='17'>$ 17.00</option>
                        </select>
                    </label>
                </div>

                <button>Add Movie</button>
            </form>
        </Fragment>
    );
}

export default AddMovie;
