import classes from './CartItem.module.css';

const CartItem = (props) => {
    const price = `$${props.price.toFixed(2)}`;

    return (
        <li className={classes['cart-item']}>
            <div>
                <h2>{props.name}</h2>
                <h4>Show Time : {props.timing}</h4>
                <h4>Screen : {props.theater}</h4>
                {/* <h4>Seat Capacity : {props.capacity}</h4> */}
                {console.log(props.ticketAvailability)}
                {!props.ticketAvailability && <h4 style={{background:'red'}}>NO MORE TICKETS</h4>}
                <div className={classes.summary}>
                    <span className={classes.price}>{price}</span>
                    <span className={classes.amount}>x {props.amount}</span>
                </div>
            </div>
            <div className={classes.actions}>
                <button onClick={props.onRemove}>âˆ’</button>
                {!props.ticketAvailability && <button onClick={props.onAdd}>+</button>}
            </div>
        </li>
    );
};

export default CartItem;
