import { Fragment } from "react";

const MovieShwoing = props => {
    return (
        <Fragment>
            <h4 style={{ color: 'white', fontSize: '36px', marginLeft: '80px' }}>now showing</h4>
            <h4 style={{ color: 'white', fontSize: '36px', marginLeft: '80px' }}>coming soon</h4>
        </Fragment>
    );
    const dropDownChangeHandler = (event) => {
        props.onChangeFilter(event.target.value);
    }

    return (
        <div className='movie-filter'>
            <div className='movie-filter__control'>
                <label>Category </label>
                <select value={props.selected} onChange={dropDownChangeHandler}>
                    <option value="" >Select a category</option>
                    <option value='All'>All Category</option>
                    <option value='Action'>Action</option>
                    <option value='Drama'>Drama</option>
                    <option value='Horror'>Horror</option>
                </select>
            </div>
        </div>
    );
};

export default MovieShwoing;
