import AddMovie from './Components/Movies/AddMovie';
import MoviesTemp from './Components/Movies/MoviesTemp';
import AddID from './Components/Movies/AddID';
import SearchMovie from './Components/Movies/SearchMovie';

const DUMMY_SLOTS = [
  ['8-11 AM',10.00],
  ['12-3 PM',12.00],
  ['4-7 PM',15.00],
  ['8-11 PM',17.00]
];
const DUMMY_MOVIES = [
  {
    id: "tt6341832",
