    rating: 7.7,
    image: 'extra2 img',
    nowShowing: 'false',
    theater: null,
    timing: null,
    capacity: null
  },
];

function App() {
  const [movies,setMovies]=useState(DUMMY_MOVIES);
  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const updateCapacityHandler = (id,amount) =>{

    const temp=movies.map((movie)=>{
      if(movie.id===id) movie.capacity=movie.capacity-amount;
      return movie;
    });
    setMovies(temp);
  };

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        'https://react-http-6b4a6.firebaseio.com/meals.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedMeals = [];

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }

      setMeals(loadedMeals);
      setIsLoading(false);
    };

