import React, { Fragment, useRef, useCallback, useState, useEffect } from 'react';

import classes from './AddMovie.module.css';
// import validationClasses from '../UI/FormValidation.module.css';

function AddMovie(props) {
   
    const [movie, setMovie] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState("null");
    
    const [title,setTitle]=useState('');
    const [tagline,setTagline] = useState('');
    const [plot,setPlot] = useState('');
    const [releaseDate,setReleaseDate] = useState('');
    const [duration,setDuration] = useState('');
    const [imageUrl,setImageUrl] = useState('')
    const [showingStatus, setShowingStatus] = useState('coming soon');
    const [capacity, setCapacity] = useState("null");
    const [screenNumber, setScreenNumber] = useState("null");
    const [timeSlot, setTimeSlot] = useState("null");
    const [price, setPrice] = useState("null");

    const [capacityTouched,setCapacityTouched]=useState(false);

    const capacityIsValid = showingStatus ==='coming soon' || (capacity>30 && capacity<=60);
    const capacityIsInvalid =  ( !capacityIsValid && capacityTouched);

    
    let formIsValid = false;

    if (capacityIsValid) {
        formIsValid = true;
    }

    const titleChangeHandler = event => {
        setTitle(event.target.value);
    };
    const releaseDateChangeHandler = event => {
        setReleaseDate(event.target.value);
    };
    const plotChangeHandler = event => {
        setPlot(event.target.value);
    };
    const taglineChangeHandler = event => {
        setTagline(event.target.value);
    };
    const durationChangeHandler = event => {
        setDuration(event.target.value);
    };
    const imageUrlChangeHandler = event => {
        setImageUrl(event.target.value);
    };
    const statusChangeHandler = event => {
        setShowingStatus(event.target.value);
    };
    const screenNumberChangeHandler = event => {
        setScreenNumber(event.target.value);
    };
    const timeSlotChangeHandler = event => {
        setTimeSlot(event.target.value);
    };

    const priceChangeHandler = event => {
        setPrice(event.target.value);
    };
    const capacityChangeHandler = event => {
        setCapacity(event.target.value);
    };

    const capacityBlurHandler= () =>{
        setCapacityTouched(true);
    };

    const fetchMoviesHandler = useCallback(async () => {

        setIsLoading(true);
        setError(null);
        try {
            // const url = `https://imdb-api.com/en/API/Title/k_d40xdze4/${idRef.current.value}`
            const url = `https://imdb-api.com/en/API/Title/k_2e0orz7h/${props.id}`
            console.log(url)
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Something went wrong!');
            }
            const data = await response.json();
            
            const transformedMovies =
            {
                id: data.id,
                title: data.title,
                plot: data.plot,
                tagline: data.tagline,
                releaseDate: data.releaseDate,
                rating: data.imDbRating,
                tagline: data.tagline,
                duration: data.runtimeStr,
                image: data.image,
                genres:data.genreList
            };
            setMovie(transformedMovies);
        } catch (error) {
            setError(error.message);
        }
        setIsLoading(false);
    }, []);

    useEffect(() => {
        fetchMoviesHandler();
    }, [fetchMoviesHandler]);

    async function addMovieHandler(movie) {
        console.log(movie);
        const response = await fetch('https://movie-react-16994-default-rtdb.firebaseio.com/movie.json', {
            method: 'POST',
            body: JSON.stringify(movie),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const data = await response.json();
        props.addMovie(false)
    };

    function submitHandler(event) {
        event.preventDefault();

        // could add validation here...

        setCapacityTouched(true);

        if (!capacityIsValid) {
            return;
        }

        setCapacity('');
        setCapacityTouched(false);

        const movieData = {
            id: props.id,
            name: title,
            tagline: tagline,
            plot: plot,
            releaseDate: releaseDate,
            duration: duration,
            image: imageUrl,
            capacity: capacity,
            rating:movie.rating,
            genres:movie.genres,
            showingStatus:showingStatus,
            theater:screenNumber,
            timimg: timeSlot,
            price: price
        };

        addMovieHandler(movieData)
    };

    return (
        <Fragment>
            <form style={{ width: "500px", marginLeft: "90px" }} onSubmit={submitHandler}>
                <div className={classes.control} >
                    <label htmlFor='id'>IMDB id</label>
                    <input type='text' id='id' 
                    // ref={idRef} 
                    value={props.id}
                />
                </div>
                <div className={classes.control}>
                    <label htmlFor='title'>Title</label>
                    <input value={movie.title} type='text' id='title' onChange={titleChangeHandler} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='tagline'>Tagline</label>
                    <input value={movie.tagline} type='text' id='tagline' onChange={taglineChangeHandler} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='plot'>plot</label>
                    <textarea value={movie.plot} rows='5' id='plot' onChange={plotChangeHandler}></textarea>
                </div>
                <div className={classes.control}>
                    <label htmlFor='date'>Release Date</label>
                    <input value={movie.releaseDate} type='text' id='date' onChange={ releaseDateChangeHandler} />
                </div>               
                <div className={classes.control}>
                    <label htmlFor='duration'>Duration</label>
                    <input value={movie.duration} type='text' id='duration' onChange={ durationChangeHandler} />
                </div>
                <div className={classes.control}>
                    <label htmlFor='imageUrl'>Image</label>
                    <input value={movie.image} type='text' id='imageUrl' onChange={ imageUrlChangeHandler} />
                </div>
                <div className={classes.control}>
                    <label>
                        Showing Status
                        <select name='option' onChange={statusChangeHandler}>
                            <option value="coming soon">coming soon</option>
                            <option value="now showing">now showing</option>
                        </select>
                    </label>
                </div>
                {showingStatus==="true" && 
                    <div className={classes.control}>
                    <label htmlFor='capacity'>Seat Capacity</label>
                    <input type='number' id='capacity' value={capacity} 
                        onChange={capacityChangeHandler} 
                        // onChange={nameInputChangeHandler}
                        onBlur={capacityBlurHandler}
                    />
                    {capacityIsInvalid && (
                        <p className={classes['error-text']}>seat capacity must be between 30 and 60</p>
                    )}
                </div>
                } {showingStatus === "true" &&
                <div className={classes.control}>
                    <label>
                        Showtime Slot
                        <select name='option' onChange={timeSlotChangeHandler}>
                            <option >Null</option>
                            <option value='8-11 AM'>8-11 AM</option>
                            <option value='12-3 PM'>12-3 PM</option>
                            <option value='4-7 PM'>4-7 PM</option>
                            <option value='8-11 PM'>8-11 PM</option>
                        </select>
                    </label>
                </div>
                } {showingStatus === "true" &&
                <div className={classes.control}>
                    <label>
                        Screen Number
                        <select name='option' onChange={screenNumberChangeHandler}>
                            <option >Null</option>
                            <option value='1'>Screen 1</option>
                            <option value='2'>Screen 2</option>
                            <option value='3'>Screen 3</option>
                            <option value='4'>Screen 4</option>
                        </select>
                    </label>
                </div>
                } {showingStatus === "true" &&
                <div className={classes.control}>
                    <label>
                        Ticket Price
                        <select name='option' onChange={priceChangeHandler}>
                            <option >Null</option>
                            <option value='10'>$ 10.00</option>
                            <option value='12'>$ 12.00</option>
                            <option value='15'>$ 15.00</option>
                            <option value='17'>$ 17.00</option>
                        </select>
                    </label>
                </div>
                }

                <div className={classes['form-action']}>
                    {console.log(formIsValid,"fir,,,")}
                    <button disabled={!formIsValid}>Submit</button>
                </div>
            </form>
        </Fragment>
    );
}

export default AddMovie;
