import { useCallback,useState, Fragment,useEffect } from "react";
import ShowSearchedMovies from "./ShowSearchedMovies";
const SearchMovie = () =>{

    const [title, setTitle]=useState('');
    const [movies,setMovies]=useState([]);
    const[success,setSuccess]=useState(false);
    const searchMovieFetch = useCallback(async () => {

        try {
            // console.log(idRef.current.value)
            const movieName = ""
            const url = `https://imdb-api.com/en/API/SearchMovie/k_d40xdze4/${movieName}`
            console.log(url)
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Something went wrong!');
            }
            const data = await response.json();
            console.log(typeof(data.results));
            console.log(data.results)
            if(data)
                setSuccess(true);

            console.log(success)
            setMovies(data.results);
            

        } catch (error) {
            // setError(error.message);
        }
    }, []);

    const searchMovieHandler= event =>{
        event.preventDefault();
        searchMovieFetch();
    }

    useEffect(() => {
        searchMovieFetch();
    }, [searchMovieFetch]);

    const titleChangeHandler = event =>{
        event.preventDefault();
        setTitle(event.target.value)
    }

    return(
        <Fragment>
            <form onSubmit={searchMovieHandler}>
                <div>
                    <label htmlFor='title'>Movie title</label>
                    <input type='text' id='title' value={title} onChange={titleChangeHandler} />
                </div>
                <button>Search Movie</button>
            </form>
            {success &&  <ShowSearchedMovies results={movies}/>}
        </Fragment>
    )
};

export default SearchMovie;
