import { useCallback,useState, Fragment,useEffect } from "react";
const SearchMovie = () =>{

    const [title, setTitle]=useState('');

    const searchMovieHandler = useCallback(async () => {
        // event.preventDefault();
        try {
            // console.log(idRef.current.value)
            const movieName ="oxygen"
            const url = `https://imdb-api.com/en/API/SearchMovie/k_d40xdze4/${movieName}`
            console.log(url)
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Something went wrong!');
            }
            const data = await response.json();
            console.log(data.result);
           
        } catch (error) {
            // setError(error.message);
        }
    }, []);
    useEffect(() => {
        searchMovieHandler();
    }, [searchMovieHandler]);

    const titleChangeHandler = event =>{
        event.preventDefault();
        setTitle(event.target.value)
    }

    return(
        <Fragment>
            <form onSubmit={searchMovieHandler}>
                <div>
                    <label htmlFor='title'>Movie title</label>
                    <input type='text' id='title' value={title} onChange={titleChangeHandler} />
                </div>
                <button>Search Movie</button>
            </form>
        </Fragment>
    )
};

export default SearchMovie;
