import { useState, useEffect, useCallback } from 'react';
import './App.css';
import Home from './Components/Home/Home';
import Header from './Components/Header/Header';
import Footer from './Components/Footer/Footer';
import Cart from './Components/Cart/Cart';
import CartProvider from './store/CartProvider';
import MoviesTemp from './Components/Movies/MoviesTemp';

const DUMMY_SLOTS = [
  ['8-11 AM',10.00],
  ['12-3 PM',12.00],
  ['4-7 PM',15.00],
  ['8-11 PM',17.00]
];
const DUMMY_MOVIES = [
  {
    id: "tt6341832",
    name: 'Oxygen',
    releaseDate: '2019-04-12T12:48:55.000Z',
    category: 'Horror',
    rating: 8.0,
    image: 'oxygen-img',
    nowShowing: 'true',
    theater: '3',
    timing: DUMMY_SLOTS[0][0],
    capacity: 60,
    price: DUMMY_SLOTS[0][1]
  },
  {
    id: "tt13429362",
    name: 'In the Earth',
    releaseDate: '2018-04-12T12:48:55.000Z',
    category: 'Horror',
    rating: 7.0,
    image: 'in the earth img',
    nowShowing: 'true',
    theater: '1',
    timing: DUMMY_SLOTS[1][0],
    capacity: 60,
    price: DUMMY_SLOTS[1][1],
    amount: 0
  },
  {
    id: "tt0944947",
    name: 'Game Of Thrones',
    releaseDate: '2021-04-12T12:48:55.000Z',
    category: 'Drama',
    rating: 9.0,
    image: 'got-img',
    nowShowing: 'true',
    theater: '2',
    timing: DUMMY_SLOTS[2][0],
    capacity: 6,
    price: DUMMY_SLOTS[2][1]
  },
  {
    id: "tt12263384",
    name: 'Extraction 2',
    releaseDate: '2020-04-12T12:48:55.000Z',
    category: 'Action',
    rating: 7.7,
    image: 'extra2 img',
    nowShowing: 'false',
    theater: null,
    timing: null,
    capacity: null
  },
];

function App() {
  const [movies,setMovies]=useState(DUMMY_MOVIES);
  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const updateCapacityHandler = (id,amount) =>{

    const temp=movies.map((movie)=>{
      if(movie.id===id) movie.capacity=movie.capacity-amount;
      return movie;
    });
    setMovies(temp);
  };

  //  logic for api

  const [movie, setMovie] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://imdb-api.com/en/API/Title/k_d40xdze4/tt13429362');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();
      console.log(data, " ---  app.js")

      const transformedMovies =
      {
        id: data.id,
        name: data.title,
        plot: data.plot,
        tagline: data.tagline,
        releaseDate: data.releaseDate,
        rating: data.imDbRating,
        // ratingVotes: data.imDbRatingVotes
      };

      setMovie(transformedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  // console.log(movie)

  return (
    <CartProvider>
      <Header onShowCart={showCartHandler} />
      {cartIsShown && <Cart onUpdateCartCapacity={updateCapacityHandler} onClose={hideCartHandler} />}
      <Home data={movie} onUpdateCartCapacity={updateCapacityHandler} />
      {/* <MoviesTemp /> */}
      <Footer />
    </CartProvider>
  );
}

export default App;
